using genFGLM

P, x = polynomial_ring(GF(65521), "x" => 1:7)
id = ideal(P, [22896*x[1]^7 + 14681*x[1]^5*x[2]^2 + 41912*x[1]^2*x[7]^5 + 27035*x[1]*x[2]^3*x[5]*x[7]^2 + 41320*x[1]*x[7]^6 + 42708*x[2]^6*x[5] + 22981*x[5]^7 + 35051*x[5]^3*x[6]^2*x[7]^2 + 22386*x[6]^7 + 29830*x[6]^6*x[7], 56814*x[1]^6*x[3] + 50001*x[1]^3*x[3]^2*x[7]^2 + 27273*x[1]*x[2]*x[6]^3*x[7]^2 + 9627*x[1]*x[3]*x[5]^2*x[6]^2*x[7] + 41910*x[2]^7 + 16814*x[2]^5*x[4]^2 + 40975*x[2]^5*x[4]*x[5] + 21788*x[3]^7 + 5598*x[3]^2*x[4]^5 + 145*x[6]*x[7]^6, 16308*x[1]^2*x[2]^4*x[3] + 45861*x[1]*x[2]*x[4]^3*x[6]*x[7] + 1331*x[1]*x[5]^6 + 24221*x[2]^3*x[3]^3*x[6] + 45710*x[2]^3*x[4]^2*x[6]^2 + 21043*x[3]^3*x[4]^2*x[5]*x[7] + 19459*x[4]^3*x[5]^4 + 18404*x[5]^7 + 4370*x[5]^5*x[6]*x[7] + 4497*x[6]*x[7]^6]);
tim = @elapsed gen_fglm(id, target_order = :degrevlex)
println("time $(tim)")
exit()
